function graphPotencias(t, YMatrix1, i)
% Para referencia:
% YMatrix1 = [Pgen_NoIrrigation; PbenSBR; Pben]

%  Auto-generated by MATLAB on 03-Sep-2020 20:37:50

% Create figure
figure1 = figure;

% Create axes
axes1 = axes('Parent',figure1);
hold(axes1,'on');

% Create multiple lines using matrix input to plot
plot1 = plot(t,YMatrix1,'Parent',axes1);
set(plot1(1),'DisplayName','Potencia sin irrigaci√≥n','LineWidth',0.6,...
    'LineStyle','--',...
    'Color',[0 0 0]);
set(plot1(2),'DisplayName','Potencia beneficio SBR','LineWidth',1.2,...
    'Color',[0 0 1]);
set(plot1(3),'DisplayName','Potencia beneficio PD','LineWidth',0.7,...
    'Color',[1 0 0]);

% Create ylabel
ylabel('Potencia [W]','FontName','Humanst521 BT');

% Create xlabel
xlabel('Hora','FontName','Humanst521 BT');

box(axes1,'on');
hold(axes1,'off');
% Set the remaining axes properties
set(axes1,'FontName','Humanst521 BT','XGrid','on','XMinorGrid','on','YGrid',...
    'on','YMinorGrid','on');
% Create legend
legend(axes1,'show');

% -------------------------------------------------------------------------
% Guardar graficas
% Descomentar la siguiente linea para guardar la grafica en una ruta
% especifica. De lo contrario se guarda en la ruta actual
ruta = 'C:\Users\james\Desktop\graficasTEMP\';

nombreArchivo = [ruta, 'dia', num2str(i), '-potencia'];
saveas(figure1, nombreArchivo, 'svg');
close;